mapper.select:
SELECT r.review_idx, t.trade_buyer_id , t.trade_seller_id, r.review_content, r.review_star_rating FROM review r JOIN trade t ON r.review_pd_idx = t.trade_pd_idx
WHERE r.review_writer LIKE CONCAT('%', #{searchKeyword}, '%') OR r.review_content LIKE CONCAT('%', #{searchKeyword}, '%')
LIMIT #{startRow} , #{listLimit}

SELECT t.trade_idx, t.trade_seller_id, t.trade_pd_idx, p.pd_status, p.pd_subject, p.pd_first_date FROM product p JOIN trade t ON p.pd_idx = t.trade_pd_idx;
WHERE trade_buyer_id LIKE CONCAT('%', #{searchKeyword}, '%') OR trade_seller_id LIKE CONCAT('%', #{searchKeyword}, '%')
LIMIT #{startRow} , #{listLimit}
