mapper.insert:
INSERT 
INTO member
VALUES (
	  #{member_id}
	, #{member_nickname}
	, #{member_passwd}
	, #{member_name}
	, #{member_birth}
	, #{member_phone}
	, 1   	                    -- 회원 상태 (1: 회원, 0: 탈퇴회원)
	,'N'						-- 이메일 인증 상태 (Y: 인증됨, N: 인증되지 않음)
	, #{member_postcode}
	, #{member_address1}
	, #{member_address2}
	, #{member_profile}			-- 프로필 사진 
	, '0'						-- 관리자 여부 (1: 관리자, 0: 일반 사용자)
	, now()
)

mapper.update:
UPDATE member 
SET
	member_postcode = #{member_postcode}
	,member_address1 = #{member_address1}
	,member_address2 = #{member_address2}
	,member_passwd = #{member_passwd}
	,member_phone = #{member_phone}
	,member_profile = #{member_profile}
WHERE
	member_id = #{member_id}

mapper.select:
SELECT *
FROM member
WHERE 
    member_name = #{member_name} 
    AND member_birth = #{member_birth}
    AND member_phone = #{member_phone}

mapper.update:
UPDATE member
SET 
	member_passwd = #{member_passwd}
WHERE  
	member_id = #{member_id}

mapper.select:
SELECT *
FROM member
WHERE 
    member_phone = #{phoneNumber}

    AND member_id = #{memberId}

SELECT member_id
FROM member
WHERE member_id = #{receiver_id}
