mapper.insert:
INSERT INTO
	product
VALUES (
	null								-- pd_idx
	, #{pd_price}						-- 상품 가격
	, #{pd_subject}
	, #{pd_content}
	, now()								-- 작성일 (pd_first_date)
	, null								-- update date (pd_update_date)
	, 1									-- 조회수 (pd_readcount)
	, pd_idx + 1						-- re_ref
	, '판매중'							-- 상품상태(pd_status)
	, #{member_id}						-- 상품 작성자
	, #{pd_category}					-- 상품 카테고리(공통코드)
	, #{pd_image1}
	, #{pd_image2}
	, #{pd_image3}
	, #{pd_image4}
	, #{pd_image5}
)

mapper.select:
SELECT *
FROM product
WHERE
	pd_category = #{pd_category}
	and pd_status = #{pd_status}

mapper.update:
UPDATE product
SET pd_status = #{pd_status}
WHERE pd_idx = #{pd_idx}

UPDATE product
SET 
	pd_price = #{pd_price}
	, pd_content = #{pd_content}
		
			, pd_image1 = #{pd_image1}
		
		
			, pd_image2 = #{pd_image2}
		
		
			, pd_image3 = #{pd_image3}
		
	, pd_category = #{pd_category}
WHERE
	pd_idx = #{pd_idx}

mapper.delete:
DELETE
	FROM likes
WHERE
	member_id = #{member_id}
	AND pd_idx = #{pd_idx}

mapper.select:
SELECT count(like_idx)
FROM likes
WHERE 
	member_id = #{member_id}
	AND pd_idx = #{pd_idx}

mapper.update:
UPDATE trade
SET trade_status = #{trade_status}
WHERE trade_idx = #{trade_idx}
