mapper.select:
SELECT member_id
FROM member
WHERE member_nickname = #{q}

SELECT search_count
FROM search
WHERE search_content = #{keyword}
UNION ALL
SELECT 0 AS search_count
WHERE NOT EXISTS (
    SELECT search_count
    FROM search
    WHERE search_content = #{keyword}
)

mapper.update:
UPDATE search
SET search_count = search_count + 1
WHERE search_content = #{keyword}

UPDATE search
SET delete_status = 1
WHERE search_content = #{content}
