mapper.select:
SELECT * FROM sms_auth_info
WHERE phone_number = #{phoneNumber}
AND auth_code = #{authCode}

mapper.update:
UPDATE sms_auth_info
SET auth_code = #{authCode}, status = #{status}
WHERE phone_number = #{phoneNumber}

UPDATE sms_auth_info
SET status = 'VERIFIED'
WHERE user_id = #{userId}
